var res={s:0,m:0,h:0,w:0,d:0,M:0,Y:0,str:""};
res.s=parseInt(msg.payload[0].toString(16));
res.m=parseInt(msg.payload[1].toString(16));
res.h=parseInt(msg.payload[2].toString(16));
res.w=parseInt(msg.payload[3].toString(16));
res.d=parseInt(msg.payload[4].toString(16));
res.M=parseInt(msg.payload[5].toString(16));
res.Y=parseInt(msg.payload[6].toString(16));

var week="";
if(res.w==1){
    week="日";
}
else if(res.w==2){
    week="月";
}
else if(res.w==3){
    week="火";
}
else if(res.w==4){
    week="水";
}
else if(res.w==5){
    week="木";
}
else if(res.w==6){
    week="金";
}
else if(res.w==7){
    week="日";
}
res.str= "20"+res.Y+"/"+res.M+"/"+res.d+"("+ week +") "+res.h+":"+res.m+":"+res.s;
msg.payload=res;
return msg;

var nowdate=new Date();

var year = nowdate.getFullYear();     // 年(4桁の西暦)
var mon  = nowdate.getMonth() + 1;    // 月(1～12)
var date = nowdate.getDate();         // 日(1～31)
var week = nowdate.getDay();          // 曜日(※0～6)
var hour = nowdate.getHours();        // 時(0～23)
var min  = nowdate.getMinutes();      // 分(0～59)
var sec  = nowdate.getSeconds();      // 秒(0～59)


var buf = new Buffer(12); 
buf[0]=0x53;//start
buf[1]=0x64;//i2c addr (0x32<1)
buf[2]=0x08;//send data 1
buf[3]=0x00;//offset 0
buf[4]=parseInt(sec, 16);
buf[5]=parseInt(min, 16);
buf[6]=parseInt(hour, 16);
buf[7]=parseInt(week, 16)+1;
buf[8]=parseInt(date, 16);
buf[9]=parseInt(mon, 16);
buf[10]=parseInt(year%1000, 16);
buf[11]=0x50;//stop
msg.payload = buf;
return msg;
